import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-appreciation-editor-modal',
  templateUrl: './appreciation-editor-modal.component.html',
  styleUrls: ['./appreciation-editor-modal.component.css']
})
export class AppreciationEditorModalComponent {

  @Input() employeeName!: string;
  @Output() closed = new EventEmitter<void>();
  // MODIFY: New event emitted when user clicks back arrow to return to employee selection
  @Output() back = new EventEmitter<void>();

  userText: string = '';
  aiText: string = '';
  showAiSuggestion: boolean = false;
  score: number = 55;

  guideItems = [
    { label: 'Language Check', status: 'error' },
    { label: 'Be specific', status: 'success' },
    { label: 'Highlight impact', status: 'success' },
    { label: 'Acknowledge effort', status: 'success' },
    { label: 'Reinforce consistency', status: 'success' }
  ];

  private lastGeneratedFor: string = '';

  onTextChange(): void {
    const text = this.userText.trim();

    if (text.length >= 20) {
      this.showAiSuggestion = true;

      if (text !== this.lastGeneratedFor) {
        this.aiText =
          'Thank you for your dedication and commitment. Your timely delivery and consistent effort have made a meaningful impact on the team, and your contribution is sincerely appreciated.';
        this.lastGeneratedFor = text;
      }
    } else {
      this.showAiSuggestion = false;
      this.lastGeneratedFor = '';
    }
  }

  rewriteWithAI(): void {
    // Later: call backend AI rewrite API
  }

  useAiText(): void {
    this.userText = this.aiText;
  }

  close(): void {
    this.closed.emit();
  }

  // MODIFY: Called when user clicks back arrow; emits 'back' event so parent can show selection modal again
  goBack(): void {
    this.back.emit();
  }
}
<div class="modal-overlay">
  <div class="modal-container">

    <!-- Header -->
    <div class="header">
      <!-- MODIFY: Make back arrow clickable to go back to employee selection -->
      <span class="back-arrow" (click)="goBack()">← You are now appreciating {{ employeeName }}</span>
      <span class="close" (click)="close()">✕</span>
    </div>

    <div class="container">

      <!-- Left panel -->
      <div class="left">
        <textarea
                class="main-text"
                [(ngModel)]="userText"
                (input)="onTextChange()"
        ></textarea>

        <div class="char-count">{{ userText.length }}</div>

        <button
                class="rewrite-btn"
                [disabled]="userText.length < 20"
                (click)="rewriteWithAI()"
        >
          ✨ Rewrite with AI
        </button>

        <div class="ai-suggestion" *ngIf="showAiSuggestion">
          <div class="ai-label">✨ AI Generated Suggestion</div>
          <textarea class="ai-text" [value]="aiText" readonly></textarea>
          <button class="use-btn" (click)="useAiText()">USE</button>
        </div>
      </div>

      <!-- Right panel -->
      <div class="right">
        <div class="score-wrapper">
          <div class="circle">
            <span>{{ score }}%</span>
          </div>
          <div class="score-text">
            <strong>This is top tier.</strong><br />
            You’re capturing exactly what makes them a star.
          </div>
        </div>

        <div class="guide-title">Your Recognition Guide</div>

        <div
                class="guide-item"
                *ngFor="let item of guideItems"
                [ngClass]="item.status"
        >
          <span>{{ item.label }}</span>
          <span class="icon">
            {{ item.status === 'success' ? '✓' : '✕' }}
          </span>
        </div>
      </div>

    </div>
  </div>
</div>
