import { Component, EventEmitter, Output } from '@angular/core';

interface Employee {
  id: number;
  name: string;
  alreadyAppreciated?: boolean;
}

@Component({
  selector: 'app-appreciation-modal',
  templateUrl: './appreciation-modal.component.html',
  styleUrls: ['./appreciation-modal.component.css']
})
export class AppreciationModalComponent {
  @Output() close = new EventEmitter<void>();

  // Mock employee list - replace with actual API call
  employees: Employee[] = [
    { id: 1, name: 'Sanjoy Debnath', alreadyAppreciated: false },
    { id: 2, name: 'Vishal Kumar Singh', alreadyAppreciated: false },
    { id: 3, name: 'Priya Sharma', alreadyAppreciated: true },
    { id: 4, name: 'Rahul Verma', alreadyAppreciated: false }
  ];

  selectedEmployeeId: number | null = null;

  selectEmployee(employeeId: number): void {
    this.selectedEmployeeId = employeeId;
  }

  proceed(): void {
    if (this.selectedEmployeeId) {
      // Navigate to appreciation page or open next modal
      console.log('Selected employee ID:', this.selectedEmployeeId);
      // TODO: Navigate to appreciation text page
      alert(`Proceeding to appreciate employee ${this.selectedEmployeeId}`);
      this.closeModal();
    }
  }

  closeModal(): void {
    this.close.emit();
  }
}
<div class="modal-overlay" (click)="closeModal()">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <div class="modal-header">
      <h3>Who deserves an appreciation today?</h3>
      <button class="close-btn" (click)="closeModal()" aria-label="Close">&times;</button>
    </div>

    <div class="modal-body">
      <div class="search-box">
        <input 
          type="text" 
          placeholder="Search employees..." 
          class="search-input"
        />
      </div>

      <div class="employee-list">
        <div 
          *ngFor="let employee of employees"
          class="employee-item"
          [class.selected]="selectedEmployeeId === employee.id"
          [class.already-appreciated]="employee.alreadyAppreciated"
          (click)="!employee.alreadyAppreciated && selectEmployee(employee.id)"
        >
          <div class="employee-info">
            <div class="employee-avatar">
              {{ employee.name.charAt(0) }}
            </div>
            <span class="employee-name">{{ employee.name }}</span>
            <span 
              *ngIf="employee.alreadyAppreciated" 
              class="appreciated-badge"
            >
              Already Appreciated
            </span>
          </div>
          <button 
            class="add-btn"
            [disabled]="employee.alreadyAppreciated"
            (click)="selectEmployee(employee.id); $event.stopPropagation()"
          >
            <span *ngIf="selectedEmployeeId === employee.id">✓</span>
            <span *ngIf="selectedEmployeeId !== employee.id">+</span>
          </button>
        </div>
      </div>
    </div>

    <div class="modal-footer">
      <button class="cancel-btn" (click)="closeModal()">
        Cancel
      </button>
      <button 
        class="next-btn" 
        (click)="proceed()"
        [disabled]="!selectedEmployeeId"
      >
        Next
      </button>
    </div>
  </div>
</div>
